import React, {useEffect, useState} from 'react';
import { useLocation } from "react-router-dom"
import { Jumbotron } from 'react-bootstrap';
import StarRatingComponent from 'react-star-rating-component';
import { signInWithGoogle, signInWithFacebook, signInWithEmail, auth, logOut } from "../utils/firebaseConfig";
import firebase from "../utils/firebaseConfig";



const GameDetail = () => {

  const location = useLocation();
  const game = location.state.game;
  const [isSignedIn, setSignedIn] = useState(false);

  useEffect(() => {
    firebase.auth().onAuthStateChanged((user) => {
      setSignedIn(!!user);
      console.log(isSignedIn)
    });
  }, []);


  return (
    <div className="gameDetail">
      {isSignedIn ? (
        <p>dsd</p>
      ) : (
        <>

      <Jumbotron>
        <div className="Jumbotron">
          <div className="Title">
            <h1>{game.name}</h1>
          </div>
          <div className="img-detail-game">
            <img  src={game.background_image} className="backImage" alt="img" />
          </div>
        </div>
      </Jumbotron>
      <div className="body">
        <div className="rating">
          <h3>Notes du jeu : </h3>
          <StarRatingComponent 
            name="rate2" 
            editing={false}
            starCount={5}
            value={game.rating}
          />
          <p>{game.rating}</p>
          <br></br>
        </div>
        <div className="released">
          <h3>Date de sortie : </h3>
          <p>{game.released}</p>
        </div>
        <div className="platforms">
          <h3>Plateformes : </h3>
          <p key={game.platforms.id}>{game.platforms.map(p => `${p.platform.name} | `)}</p>
        </div>
        <div className="stores" >
          <h3>Genre : </h3>
          <p key={game.genres.id}>{game.genres.map(m => `${m.name}  `)}</p>
        </div>
      </div>
      <div className="screenshot">
        <p>{game.short_screenshots.map(p =><img  className="screen" key={p.name} src={p.image}  alt='screenshot'></img>)}</p>
      </div>
        </>
      )}
    </div>
  );
};


export default GameDetail;
