import React, { useState, useEffect } from 'react';
import firebase, { db } from '../../utils/firebaseConfig';

const Read = () => {

  const [comment, setComment] = useState("");

  useEffect(() => {
    db.collection("comments").onSnapshot((snapshot) => {
      setComment(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          comment: doc.data()
        }))
      );
    });
    console.log(comment);
  }, []);

  return (
    <div className="Read">
      <ul>
      {comment &&
      comment.map(({comment, id}) => (
        <div className="commentBody" key={id}>
          <p>{comment.text}</p>
        </div>
        ))}
      </ul>



    </div>
  );
};

export default Read;
