import { db } from '../utils/firebaseConfig';

const Dashboard = () => {


  const [comment, setComment] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {

    db.collection("Comments")
    .onSnapshot(snapshot => {
      setComments(
        snapshot.docs
      );
    });
  }, []);

  console.log(comments);

  const Comment = () =>{
    comments.map((comment) => setComment(comment))
  } 
  Comment();
  console.log(comment)

