const Nav = () => {


  return (
    <div className="nav">
     {isSignedIn ? (
       <div className="deco"  onClick={() => firebase.auth().signOut()}>deco</div>
     ) : ( 

      <NavLink exact to='/SignUp' activeClassName="nav-active">Connexion</NavLink>

     )}


    </div>
  );
};

export default Nav;
