import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";

const Nav = () => {
  const { currentUser } = useContext(AuthContext);

  return (
        <nav className="nav">
        {currentUser && 
        <>
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink to="/Dashboard" activeClassName="nav-active">
          Profil
        </NavLink>
        </>
        }
          <NavLink exact to="/SignUp" activeClassName="nav-active">
            Connexion
          </NavLink>
        <div className="title-project">
          <h1>TO</h1>
        </div>
      </nav>

  );
};

export default Nav;
