import { AuthContext } from "../../providers/users/UserProvider";
import { db } from "../../utils/firebaseConfig";

export const createComment = (comment, gameId) => {

  db.collection("comments").add({
    author: AuthContext.displayName,
    gameId: gameId,
    text: comment
  });
};
