import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink } from "react-router-dom";
import { AuthContext, UidContext } from "../providers/users/UserProvider";

const Nav = () => {
  const { uid } = useContext(UidContext);

  return (
        <div className="nav">
        {uid ? (
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
        ) : (
          <NavLink exact to="/SignUp" activeClassName="nav-active">
            Connexion
          </NavLink>
        )}
      </div>

  );
};

export default Nav;
