import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";

const Nav = () => {
  const { currentUser } = useContext(AuthContext);

  return (
    <nav>
      <div className="nav">
        {currentUser ? (
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deco
          </NavLink>
        ) : (
          <NavLink exact to="/SignUp" activeClassName="nav-active">
            Connexion
          </NavLink>
        )}
      </div>
    </nav>
  );
};

export default Nav;
