import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";

const Nav = () => {
  const [id, setId] = useState("")

  const { currentUser } = useContext(AuthContext);

  const setUserId = () => {
    setId(currentUser.uid)
  }



  return (
        <nav className="nav">
        {currentUser && 
        <>
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink to={`/Dashboard${id}`} activeClassName="nav-active" onClick={setUserId}>
          Profil
        </NavLink>
        </>
        }
        {!currentUser &&
        <NavLink exact to="/SignUp" activeClassName="nav-active">
          Connexion
        </NavLink>
        }
        <NavLink exact to="/" activeClassName="nav-active">
          Home
        </NavLink>  
        <div className="title-project">
          <h1>TO</h1>
        </div>
      </nav>

  );
};

export default Nav;
