import { db } from "../../utils/firebaseConfig";
import { useEffect, useState } from "react";

export const createComment = (comment, game, currentUser, profil) => {

  if (currentUser === null) {
    return;
  }
  return db
    .collection("Comments")
    .add({
      author: djdskd,
      gameId: game.id,
      text: comment,
      profilID: profil.uid

    })
    .then(() => {
      console.log("createComment OK");
      return true;
    })
    .catch(reason => {
      console.log("createComment Error : " + reason);
      return false;
    });
};

export const updateComment = (updatedText, comment, currentUser) => {
  if (currentUser === null) {
    return;
  }

  return db
    .collection("Comments")
    .doc(comment.id)
    .set({
      author: comment.data().author,
      gameId: comment.data().gameId,
      profilID: comment.data().profilID,
      text: updatedText
    })
    .then(() => {
      console.log("updateComment OK");
      return true;
    })
    .catch(reason => {
      console.log("updateComment Error : " + reason);
      return false;
    });
    deleteComment()
};

export const deleteComment = (comment, currentUser) => {
  if (currentUser === null) {
    return;
  }

  return db
    .collection("Comments")
    .doc(comment.id)
    .delete()
    .then(() => {
      console.log("deleteComment OK");
      return true;
    })
    .catch(reason => {
      console.log("deleteComment Error : " + reason);
      return false;
    });
};
