import React, { useEffect, useState } from 'react';
import GameList from '../components/GameList';

const Home = () => {

  useEffect(() => {
    fetchGames()
  }, []);

  const [games, setGames] = useState([]);

  const fetchGames = () => {
    fetch('https://api.rawg.io/api/games?key=21dd3ea253de44a6bc6c827eedc10926&page_size=30')
    .then(resp => resp.json)
    .then(({ results }) => setGames(results));
  }



  return (
    <div className="home">
      <GameList games={games} />
    </div>
  );
};

export default Home;
