import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink, useHistory } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";
import { ReadProfil } from "./profils/ProfilsFirestoreService";

const Nav = () => {

  const { currentUser } = useContext(AuthContext);
  const [id, setId] = useState("");

  const LinkId = () => {
    setId(currentUser.uid);
  }





  return (
        <nav className="nav">
        {currentUser && 
        <>
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink to={`/Dashboard/${currentUser.displayName}`}  activeClassName="nav-active" onClick={LinkId}>
          Profil
        </NavLink>
        </>
        }
        {!currentUser &&
        <NavLink exact to="/SignUp" activeClassName="nav-active">
          Connexion
        </NavLink>
        }
        <NavLink exact to="/" activeClassName="nav-active">
          Home
        </NavLink>  
        <div className="title-project">
          <h1>TO</h1>
        </div>
      </nav>

  );
};

export default Nav;
