import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut } from "../utils/firebaseConfig";
import { NavLink, useHistory } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";

const Nav = () => {
  const { currentUser } = useContext(AuthContext);
  let goToProfil = false;

  const onClickEvent = () => {
    goToProfil = true;
  };

  return (
    <nav className="nav">
      {currentUser && (
        <>
          <NavLink to="/"    onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink
            to={{
              pathname: "/Profil",
              state: { profilID: currentUser.uid }
            }}
            isActive={goToProfil}
            onClick={onClickEvent()}
          >
            Profil
          </NavLink>
        </>
      )}
      {!currentUser && (
        <NavLink exact to="/SignUp" activeClassName="nav-active">
          Connexion
        </NavLink>
      )}
      <NavLink exact to="/" activeClassName="nav-active">
        Home
      </NavLink>
      <div className="title-project">
        <h1>TO</h1>
      </div>
    </nav>
  );
};

export default Nav;
