  const currentUser = useContext(AuthContext);

  const [comments, setComments] = useState([]);
  const [profils, setProfils] = useState([]);

  useEffect(() => {
    db.collection("Comments")
    .onSnapshot(snapshot => {
      setComments(
        snapshot.docs
