import React, { useEffect, useState } from "react";


const GameList = () => {


  useEffect(() => {
    fetchGames()
    .finally(() => {
      setLoading(false);
    });
  }, []);

  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchGames = () => {
    setLoading(false);
    fetch('https://api.rawg.io/api/games?key=21dd3ea253de44a6bc6c827eedc10926&page_size=30')
    .then(resp => resp.json)
    .then(({ results }) => setGames(results));
  }

  if (loading) {
    return <p>Data is loading...</p>;
  }

  return (
    <div className="GameList">
      {games.map(game => 
        <div  className="name" key={game.id}>
          <h1>{game.name}</h1>
        </div>
      )};
    </div>
  );
};

export default GameList;
