import React, { useState, useEffect, useContext } from "react";

import { AuthContext } from "../../providers/users/UserProvider";
import { db } from "../../utils/firebaseConfig";

const FirestoreComment = () => {
  //const [author, setAuthor] = useState(currentUser.displayName);

  const createComment = (comment: String, gameId: String) => {
    const { currentUser } = useContext(AuthContext);

    db.collection("comments").add({
      author: currentUser.displayName,
      gameId: gameId,
      text: comment
    });
  };
};

export default FirestoreComment;
