import React, { useState, useEffect, useContext } from "react";
import firebase, { logOut, db } from "../utils/firebaseConfig";
import { NavLink, useHistory } from "react-router-dom";
import { AuthContext } from "../providers/users/UserProvider";

const Nav = () => {
  const [profils, setProfils] = useState([]);

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
      db.collection("Profil")
    .onSnapshot(snapshot => {
      setProfils(
        snapshot.docs
      );
    });

  }, []);

  let history = useHistory();


  function handleClick(profils) {
    history.push({
      pathname: '/GameDetail',
      search : `?id=${profils.uid}`,
      state: { profils: profils}
    });
  }





  return (
        <nav className="nav">
        {currentUser && 
        <>
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink to="/Dashboard" activeClassName="nav-active" onClick={handleClick(profils)}>
          Profil
        </NavLink>
        </>
        }
        {!currentUser &&
        <NavLink exact to="/SignUp" activeClassName="nav-active">
          Connexion
        </NavLink>
        }
        <NavLink exact to="/" activeClassName="nav-active">
          Home
        </NavLink>  
        <div className="title-project">
          <h1>TO</h1>
        </div>
      </nav>

  );
};

export default Nav;
