import React, { useState, useEffect, useContext } from "react";
import firebase, { db } from "../../utils/firebaseConfig";
import { useLocation } from "react-router-dom";
import UpdateDelete from "./UpdateDelete";

const Read = ({ game }) => {
  const [comment, setComment] = useState("");

  useEffect(() => {
    db.collection("comments").onSnapshot(snapshot => {
      setComment(
        snapshot.docs.map(doc => ({
          id: doc.id,
          comment: doc.data()
        }))
      );
    });
  }, []);

  console.log(comment);
  console.log(game.id);

  return (
    <div className="Read">
      <ul>
        {comment &&
          comment.map(({ comment, id }) => (
            <div className="commentBody" key={id}>
              {comment.gameId === game.id && (
               <UpdateDelete comment={comment} />

              )}
            </div>
          ))}
      </ul>
    </div>
  );
};

export default Read;
