import React, {useState, useEffect, useContext} from 'react';
import firebase, { db, auth } from '../../utils/firebaseConfig';
import { AuthContext } from '../../providers/users/UserProvider';




const Create = ({game}) => {
  const { currentUser } = useContext(AuthContext);


  const [text, setText] = useState("");
  //const [author, setAuthor] = useState(currentUser.displayName);


  const createComment = () => {
    db.collection("comments").add({
      game: game.id,
      text: text,

    });
    setText("");

  }


  return (
    <div className="create">
      {currentUser ? (
        <>
      <h4>Commenter :</h4>
      <div className="form">
        <textarea
          type="text"
          placeholder="Citation"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={createComment}>Create</button>
      </div>
      </>
       ) : (
        <div className="noUser">
          <p>Co toi pour commenter</p>
        </div>
       )} 
    </div>
  );
};

export default Create;
