import React, { useEffect, useState } from "react";


const GameList = () => {

  useEffect(() => {
    fetchGames()
  }, []);

  const [games, setGames] = useState([]);

  const fetchGames = () => {
    fetch('https://api.rawg.io/api/games?key=21dd3ea253de44a6bc6c827eedc10926&page_size=30')
    .then(resp => resp.json)
    .then(({ results }) => setGames(results));
  }

  return (
    <div className="GameList">
      {games.map(game => 
        <div className="container" key={game.id}>
          <div className="game-card">
            <div className="img-game">
              <img src={game.background_image} alt="img" />
            </div>
            <div className="card-body">
              <h3 className="title-game">{game.name}</h3>
              <p>Date de sortie: {game.released}</p>

            </div>
          </div>
        </div>
      )};
    </div>
  );
};

export default GameList;
