    setUserProfilId();
    profilHistory.push({
      pathname: '/Dashboard',
      search : `?id=${currentUser.uid}`,
      state: {currentUser : currentUser}
    });
  }


  return (
        <nav className="nav">
        {currentUser && 
        <>
          <NavLink to="/" activeClassName="nav-active" onClick={logOut}>
            Deconnexion
          </NavLink>
          <NavLink to="/Dashboard" activeClassName="nav-active" onClick={handleClick}>
          Profil
        </NavLink>
        </>
        }
        {!currentUser &&
        <NavLink exact to="/SignUp" activeClassName="nav-active">
          Connexion
        </NavLink>
        }
        <NavLink exact to="/" activeClassName="nav-active">
          Home
        </NavLink>  
        <div className="title-project">
          <h1>TO</h1>
        </div>
      </nav>

  );
};

export default Nav;
