import { db } from "../../utils/firebaseConfig";

export const createComment = (comment, gameId, currentUser) => {
  if (currentUser === null) {
    return;
  }
  return db.collection("Comments").add({
    author: currentUser.displayName,
    gameId: gameId,
    text: comment,
    profilID: currentUser.uid
  });
};

export const updateComment = (updatedText, comment, currentUser) => {

  if (currentUser === null) {
    return;
  }

  return db.collection("Comments")
  .doc(comment.id)
  .set({author: comment.data().author, gameId: comment.data().gameId, 
    profilID: comment.data().profilID, text: updatedText})
  .then(() => {
    console.log("updateComment OK")
    return true
  })
  .catch(reason => {
    console.log("updateComment Error : " + reason)
    return false
  })
};
