import React, { useState, useContext } from 'react';
import { db } from "../../utils/firebaseConfig";
import { AuthContext } from '../../providers/users/UserProvider';

const UpdateDelete = ({comment}) => {
const [update, setUpdate] = useState(false);
const [updateDoc, setUpdateDoc] = useState("");
const [updateText, setUpdateText] = useState("");

const {currentUser} = useContext(AuthContext);

const authorCheck = () => {
  if (comment.uid === currentUser.uid) {
    return true;
  } else {
    return false;
  }
};
authorCheck();

const updateComment = (e) => {

  db.collection("comments").doc(update).update({
    text: updateText
  });

  setUpdateText("");
  setUpdate(false);
}

  return (
    <div className="UpdateDelete">
     <li>
     {update === false && (
        <div className="comment-container">
          <p>"{comment.text}"</p>


          {authorCheck() && (
            <div className="buttons-container">
              <button onClick={() => setUpdate(!update)}>Update</button>
            </div>
          )}
        </div>
      )}
       {update && (
        <div className="item-container-update">
          <textarea
            defaultValue={comment.text}
            onChange={(e) => setUpdateText(e.target.value)}
          />
          <button onClick={updateComment}>Validate update</button>
        </div>
      )}

     </li>
    </div>
  );
};

export default UpdateDelete;
