import React, { useEffect, useState } from "react";


const GameList = () => {

  useEffect(() => {
    fetchGames()
  }, []);

  const [games, setGames] = useState([]);

  const fetchGames = () => {
    fetch('https://api.rawg.io/api/games?key=21dd3ea253de44a6bc6c827eedc10926&page_size=30')
    .then(resp => resp.json)
    .then(({ results }) => setGames(results));
  }

  return (
    <div className="GameList">
      {games.map(game => (
        <div key={game.id}>
          <h1>{game.name}</h1>
        </div>
      ))};
    </div>
  );
};

export default GameList;
